I1 = 1.4142;f = 100;w = 2 * pi * f;T = 1 / f;R1 = 150;L1 = 320e-3;C2 = 15e-6;#сопротивленияXl = complex(0, w * L1);Xc = complex(0, -1/(w * C2));Zlc = Xl + Xc;Z = (R1 * Zlc) / (R1 + Zlc)#токи в ветвяхE = I1 * Z#E_abs = abs(E)Ir1 = E / R1#Ir1_abs = abs(Ir1)Ilc = E / Zlc#Ilc_abs = abs(Ilc)Ulc = Ilc * Z;#Ulc_abs = abs(Ulc)Ur1 = Ir1 * R1;#Ur1_abs = abs(Ur1)#баланс мощностейP_source = E * IP_source_active = real(P_source)P_source_reactive = imag(P_source)P_consumer = Ulc * Ilc + Ur1 * Ir1P_consumer_active = real(P_consumer)P_consumer_reactive = imag(P_consumer)figurehold on, grid ontitle('Векторная диаграмма токов')compass(I1, 'r')compass(Ir1, 'g')compass(Ilc, 'b')legend('I', 'Ir1', 'Ilc')figurehold on, grid ontitle('Векторная диаграмма напряжений')compass(E, 'r')compass(Ulc, 'g')compass(Ur1 * 0.8, 'b')legend('E', 'Ulc', 'Ur1')